mapper.select:
{call proc_login(#{cmng_id,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_pw,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_name,mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{msg,mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
				)}

mapper.insert:
{call proc_join(#{dept_no,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_id,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_pw,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_name,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_grade,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_hp,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{cmng_name,mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
				,#{msg,mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
				)}
