mapper.select:
select dept_name
from dept
where com_no = #{com_no}

select visit_no, visit_apply_date, visit_desti, visit_type
		,visit_purps, visit_date, visit_term, visit_day
from visit_apply
where com_no = #{com_no}
and visit_apply_name = #{visit_apply_name}
and visit_apply_hp = #{visit_apply_hp}
and visit_apply_date >= #{visit_apply_date1}
 and visit_apply_date <= #{visit_apply_date2}

select visitor_name, visitor_hp, visit_no
from visitor_mng
where visit_no = #{visit_no}

select tkin_model, tkin_kind, tkin_posbl_encc, tkin_brand, visit_no
from tkin_mng
where visit_no = #{visit_no}

select parking_kind, parking_num, parking_model, visit_no
from parking_mng
where visit_no = #{visit_no}

mapper.insert:
		insert into visit_apply (com_no, visit_no, visit_apply_name, visit_apply_date,  visit_purps
                                ,visit_desti, visit_type, visit_date, visit_apply_hp, visit_vhcle_encc
                                ,visit_tkin_encc, visit_permit_id, visit_permit_st, visit_notes
                                ,visit_term, visit_day)
				values (#{com_no}, #{visit_no}, #{visit_apply_name}, #{visit_apply_date}, #{visit_purps}
		               ,#{visit_desti}, #{visit_type}, #{visit_date}, #{visit_apply_hp}, #{visit_vhcle_encc}
		               ,#{visit_tkin_encc}, #{visit_permit_id}, #{visit_permit_st}, #{visit_notes}
		               ,#{visit_term}, #{visit_day}
		               )

mapper.select:
select visit_no, visit_apply_date, com_name, visit_purps, visit_date, visit_permit_st
		from visit_apply vi, company co
		where vi.com_no = co.com_no
		and visit_apply_name = #{visit_apply_name}
		and visit_apply_hp = #{visit_apply_hp}
